'use client';
import Image from 'next/image';
import React from 'react';
import { motion } from 'framer-motion';

const ContinuousShakeRingButton = () => {
	return (
		<div className='flex items-center justify-center absolute bottom-0 z-10 left-[50%] translate-x-[-50%] mb-10'>
			<style jsx>{`
				@keyframes ring-wave {
					0% {
						transform: scale(0.9);
						opacity: 0.9;
					}
					50% {
						transform: scale(1.4);
						opacity: 0.4;
					}
					100% {
						transform: scale(1.8);
						opacity: 0;
					}
				}

				@keyframes shake-continuous {
					0%,
					100% {
						transform: translateX(0) translateY(0) rotate(0deg);
					}
					10% {
						transform: translateX(-2px) translateY(-1px) rotate(-0.5deg);
					}
					20% {
						transform: translateX(2px) translateY(1px) rotate(0.5deg);
					}
					30% {
						transform: translateX(-2px) translateY(1px) rotate(-0.5deg);
					}
					40% {
						transform: translateX(2px) translateY(-1px) rotate(0.5deg);
					}
					50% {
						transform: translateX(-1px) translateY(-1px) rotate(0deg);
					}
					60% {
						transform: translateX(1px) translateY(1px) rotate(0.5deg);
					}
					70% {
						transform: translateX(-1px) translateY(1px) rotate(-0.5deg);
					}
					80% {
						transform: translateX(1px) translateY(-1px) rotate(0.5deg);
					}
					90% {
						transform: translateX(-1px) translateY(0) rotate(0deg);
					}
				}

				.ring-wave {
					animation: ring-wave 2.5s infinite;
				}

				.shake-continuous {
					animation: shake-continuous 1.2s infinite;
				}
			`}</style>

			<div className='relative'>
				{/* Ring Effects with Wave Animation */}
				<div className='absolute inset-0 -m-10'>
					<div className='w-full h-full rounded-full border-4 border-sky-400 ring-wave'></div>
				</div>
				<div className='absolute inset-0 -m-8'>
					<div
						className='w-full h-full rounded-full border-3 border-sky-500 ring-wave'
						style={{ animationDelay: '0.3s' }}
					></div>
				</div>
				<div className='absolute inset-0 -m-6'>
					<div
						className='w-full h-full rounded-full border-3 border-sky-600 ring-wave'
						style={{ animationDelay: '0.6s' }}
					></div>
				</div>
				<div className='absolute inset-0 -m-4'>
					<div
						className='w-full h-full rounded-full border-2 border-sky-700 ring-wave'
						style={{ animationDelay: '0.9s' }}
					></div>
				</div>

				{/* Main Button with Continuous Shake */}
				<a
					href='mailto:admin@bachkhoa.tech'
					className='relative flex items-center gap-2 bg-[#19376B] hover:bg-sky-800 px-6 py-3 rounded-full text-white font-bold cursor-pointer transition-all duration-300 transform hover:scale-110 shadow-2xl hover:shadow-purple-500/30 border border-purple-400/30 shake-continuous'
				>
					<span>ĐĂNG KÝ NGAY</span>
					<svg
						xmlns='http://www.w3.org/2000/svg'
						width={30}
						height={30}
						viewBox='0 0 50 50'
						fill='none'
						className='transition-transform duration-300 hover:translate-x-2'
					>
						<path
							d='M10.2688 39.7306C13.1823 42.6442 16.8945 44.6285 20.9358 45.4324C24.977 46.2362 29.1659 45.8237 32.9728 44.2469C36.7796 42.6701 40.0333 39.9998 42.3225 36.5738C44.6118 33.1477 45.8336 29.1198 45.8336 24.9994C45.8336 20.8789 44.6118 16.851 42.3225 13.4249C40.0333 9.9989 36.7796 7.32864 32.9728 5.75183C29.1659 4.17502 24.977 3.76247 20.9358 4.56636C16.8945 5.37025 13.1823 7.35447 10.2688 10.2681'
							stroke='white'
							strokeWidth={2}
						/>
						<path
							d='M31.25 25L32.875 23.6979L33.9167 25L32.875 26.3021L31.25 25ZM6.25001 27.0833C5.69747 27.0833 5.16757 26.8638 4.77687 26.4731C4.38617 26.0824 4.16667 25.5525 4.16667 25C4.16667 24.4475 4.38617 23.9176 4.77687 23.5269C5.16757 23.1362 5.69747 22.9167 6.25001 22.9167V27.0833ZM24.5417 13.2812L32.875 23.6979L29.625 26.3021L21.2917 15.8854L24.5417 13.2812ZM32.875 26.3021L24.5417 36.7188L21.2917 34.1146L29.625 23.6979L32.875 26.3021ZM31.25 27.0833H6.25001V22.9167H31.25V27.0833Z'
							fill='white'
						/>
					</svg>
				</a>
			</div>
		</div>
	);
};

